import React, { useState } from 'react';
import showPopup from './ShowPopup';
import setShowPopup from './SetShowPopup';

const schemaOptions = [
  { label: 'First Name', value: 'first_name' },
  { label: 'Last Name', value: 'last_name' },
  { label: 'Gender', value: 'gender' },
  { label: 'Age', value: 'age' },
  { label: 'Account Name', value: 'account_name' },
  { label: 'City', value: 'city' },
  { label: 'State', value: 'state' },
];

function App() {
  const [segmentName, setSegmentName] = useState('');
  const [selectedSchema, setSelectedSchema] = useState([]);
  const [newSchema, setNewSchema] = useState('');

  const handleAddSchema = () => {
    setSelectedSchema(prevSchema => [
      ...prevSchema,
      { value: newSchema, label: newSchema }
    ]);
    setNewSchema('');
  };

  const handleSaveSegment = () => {
    const segmentData = {
      segment_name: segmentName,
      schema: selectedSchema
    };
    console.log(); // replace with your API call to send data to server
  };

  return (
    <div>
      <button onClick={() => setShowPopup(true)}>Save Segment</button>

      {showPopup && (
        <div className="popup">
          <label>
            Segment Name:
            <input
              type="text"
              value={segmentName}
              onChange={(e) => setSegmentName(e.target.value)}
            />
          </label>

          <div className="blue-box">
            {selectedSchema.map((schema, index) => (
              <div key={index}>
                <select
                  value={schema.value}
                  onChange={(e) => {
                    const newSelectedSchema = [...selectedSchema];
                    newSelectedSchema[index].value = e.target.value;
                    newSelectedSchema[index].label = e.target.selectedOptions[0].label;
                    setSelectedSchema(newSelectedSchema);
                  }}
                >
                  {schemaOptions
                    .filter((option) => !selectedSchema.find(s => s.value === option.value))
                    .map((option) => (
                      <option key={option.value} value={option.value}>{option.label}</option>
                    ))
                  }
                </select>
              </div>
            ))}

            <div>
              <select
                value={newSchema}
                onChange={(e) => setNewSchema(e.target.value)}
              >
                <option value="">Select a new schema</option>
                {schemaOptions
                  .filter((option) => !selectedSchema.find(s => s.value === option.value))
                  .map((option) => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))
                }
              </select>
              <button onClick={handleAddSchema}>+ Add new schema</button>
            </div>
          </div>

          <button onClick={handleSaveSegment}>Save the Segment</button>
        </div>
      )}
    </div>
  );
}

export default App;
