import React, { useState } from 'react';
import axios from 'axios';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';


const options = [
    { label: 'First Name', value: 'first_name' },
    { label: 'Last Name', value: 'last_name' },
    { label: 'Gender', value: 'gender' },
    { label: 'Age', value: 'age' },
    { label: 'Account Name', value: 'account_name' },
    { label: 'City', value: 'city' },
    { label: 'State', value: 'state' }
];

function Segmentpage() {
    const [segmentName, setSegmentName] = useState('');
    const [schema, setSchema] = useState([{ first_name: 'First Name' }, { last_name: 'Last Name' }]);
    const [selectedOption, setSelectedOption] = useState('');
    const [availableOptions, setAvailableOptions] = useState(options.filter(opt => !schema.find(sch => sch[opt.value])));

    const handleAddSchema = () => {
        if (selectedOption && !schema.find(sch => sch[selectedOption])) {
            setSchema([...schema, { [selectedOption]: selectedOption }]);
            setSelectedOption('');
            setAvailableOptions(options.filter(opt => !schema.find(sch => sch[opt.value])));
        }
    };

    const handleSaveSegment = () => {
        const data = {
            segment_name: segmentName,
            schema: schema.map(sch => ({ [sch[sch.value]]: sch.label }))
        };
        axios.post('https://webhook.site/21ea7e89-3370-472f-95d3-95c1d7bf915d', data)
            .then(() => {
                alert('Segment saved successfully!');
                setSegmentName('');
                setSchema([{ first_name: 'First Name' }, { last_name: 'Last Name' }]);
            })
            .catch(error => alert(`Error saving segment: ${error}`));
    };

    return (
        <div className="App">
            <div className='header'>
                <div><ArrowBackIosIcon /></div>
                <div className='title1'>Saving Segment</div>
            </div>
            <div className='section1'>
                <label>Enter the Name of the segment <input type="text" value={segmentName} onChange={e => setSegmentName(e.target.value)} /></label>
                <br /><br />
                {schema.map(sch => (
                    <div key={sch.value}>
                        <label>{sch.label}:</label>
                        <select value={sch.value} onChange={e => {
                            const updatedSchema = schema.map(s => {
                                if (s.value === sch.value) {
                                    return { ...s, [e.target.value]: e.target.value };
                                } else {
                                    return s;
                                }
                            });
                            setSchema(updatedSchema);
                            setAvailableOptions(options.filter(opt => !updatedSchema.find(sch => sch[opt.value])));
                        }}>
                            {options.map(opt => (
                                <option key={opt.value} value={opt.value} disabled={sch[opt.value]}>{opt.label}</option>
                            ))}
                        </select>
                    </div>
                ))}
                <br />
                <label>
                    Add schema to segment:
                    <select value={selectedOption} onChange={e => setSelectedOption(e.target.value)}>
                        <option value="">Select an option</option>
                        {availableOptions.map(opt => (
                            <option key={opt.value} value={opt.value}>{opt.label}</option>
                        ))}
                    </select>
                    <button onClick={handleAddSchema}>+Add new schema</button>
                </label>
                <br /><br />
                <button onClick={handleSaveSegment}>Save Segment</button>
            </div>
        </div>
    );
}

export default Segmentpage;
